<?phpclass plantillaPagina {  private $titulo;  // el título (etiqueta <title>) de la página  private $cabecera;  // la cabecera de la página (dentro del <body>)  private $contenido;  // el contenido de la página (dentro del <body>, después de la cabecera)  private $pie;  // el pie de la página (dentro del <body>, después del contenido)  private $pagina;  // todo lo anterior junto    function __construct($titulo="", $cabecera="", $contenido, $pie="") {    global $firephp;    $css = "/whyb/web/css/estilos.css";    $this->pagina = <<<HTML<html><head>  <meta http-equiv="Content-Type" content="text/html; charset=utf-8" />  <link href="$css" rel="stylesheet" type="text/css"/>  <title>$titulo</title></head>  <body>$cabecera $contenido $pie</body></html>HTML; }    function mostrar() {    //print $this->pagina;    return $this->pagina;  }}?> 