<?phpclass plantillaPagina {  private $titulo;  // el título (etiqueta <title>) de la página  private $cabecera;  // la cabecera de la página (dentro del <body>)  private $contenido;  // el contenido de la página (dentro del <body>, después de la cabecera)  private $pie;  // el pie de la página (dentro del <body>, después del contenido)  private $pagina;  // todo lo anterior junto    function __construct($titulo="", $cabecera="", $contenido, $pie="") {    global $firephp;    $css = "/whyb/web/css/estilos.css";    $this->pagina = <<<HTML<html><head>  <meta http-equiv="Content-Type" content="text/html; charset=utf-8" />  <title>$titulo</title>  <script type="text/javascript" src="http://ajax.googleapis.com/ajax/libs/jquery/1.6.4/jquery.min.js"></script>  <script type="text/javascript" src="http://maps.google.com/maps/api/js?sensor=true"></script>  <script type='text/javascript' src='../../src/gmaps/gmaps.js'></script>  <link href="$css" rel="stylesheet" type="text/css"/>  <script type='text/javascript'>    var map;    $(document).ready(function(){      map = new GMaps({      el: '#map',      lat: 46.800028,      lng: 8.136319,      zoom: 4,      zoomControl : true,      });    });  </script></head>  <body>$cabecera $contenido $pie</body></html>HTML; }    function mostrar() {    //print $this->pagina;    return $this->pagina;  }}?> 