<?phpclass plantillaPagina {  private $titulo;  // el título (etiqueta <title>) de la página  private $cabecera;  // la cabecera de la página (dentro del <body>)  private $contenido;  // el contenido de la página (dentro del <body>, después de la cabecera)  private $pie;  // el pie de la página (dentro del <body>, después del contenido)  private $pagina;  // todo lo anterior junto    function __construct($titulo="", $cabecera="", $contenido, $pie="") {    global $firephp;    $css = "/whyb/web/css/estilos.css";    $this->pagina = <<<HTML<html><head>  <meta http-equiv="Content-Type" content="text/html; charset=utf-8" />  <title>$titulo</title>  <script type="text/javascript" src="http://ajax.googleapis.com/ajax/libs/jquery/1.6.4/jquery.min.js"></script>  <script type="text/javascript" src="http://maps.google.com/maps/api/js?sensor=true"></script>  <script src="http://code.jquery.com/jquery-latest.js"></script>  <script type="text/javascript" src="/whyb/vendor/paginate/jquery.paginate.js"></script>  <script type="text/javascript" src="/whyb/web/js/javascriptFunctions.js"></script>  <script type="text/javascript" src="/whyb/web/js/jqueryFunctions.js"></script>  <link href="$css" rel="stylesheet" type="text/css"/></head>  <body onload='initialize()'>  $cabecera $contenido $pie</body></html>HTML; }    function mostrar() {    return $this->pagina;  }}?> 